CREATE OR REPLACE PROCEDURE sup_valida_pessoa_autor_carga (nm_usuario_p text, cd_pessoa_autor_p text, ie_opcao_p text) AS $body$
DECLARE

 
/* ie_opcao_p 
L - Liberar 
E - Estornar 
*/
 
 
cd_pessoa_usuario_w	pessoa_fisica.cd_pessoa_fisica%type;


BEGIN 
 
cd_pessoa_usuario_w := obter_pessoa_fisica_usuario(nm_usuario_p,'C');
 
if (cd_pessoa_usuario_w <> cd_pessoa_autor_p) then 
	if (ie_opcao_p = 'L') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(234285);
	elsif (ie_opcao_p = 'E') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(234314);
	end if;
end if;
 
end;
