CREATE OR REPLACE PROCEDURE sup_vincula_nf_item_prescr ( nr_prescricao_p bigint, nr_sequencia_p bigint, nr_seq_nota_p bigint) AS $body$
DECLARE


qtd_w			bigint;
cd_cgc_emitente_w	nota_fiscal.cd_cgc_emitente%type;


BEGIN

select	count(1)
into STRICT	qtd_w
from	nota_fiscal
where	nr_sequencia = nr_seq_nota_p
and	(cd_cgc_emitente IS NOT NULL AND cd_cgc_emitente::text <> '');

if (qtd_w > 0) then
	begin

	select	max(cd_cgc_emitente)
	into STRICT	cd_cgc_emitente_w
	from	nota_fiscal
	where	nr_sequencia = nr_seq_nota_p;

	end;
end if;

if (nr_sequencia_p = 0) then

	update	prescr_material
	set	nr_seq_nf_entrada = nr_seq_nota_p, cd_fornec_consignado = CASE WHEN qtd_w=0 THEN cd_fornec_consignado  ELSE cd_cgc_emitente_w END
	where	nr_prescricao = nr_prescricao_p;

else

	update	prescr_material
	set	nr_seq_nf_entrada = nr_seq_nota_p, cd_fornec_consignado = CASE WHEN qtd_w=0 THEN cd_fornec_consignado  ELSE cd_cgc_emitente_w END
	where	nr_prescricao = nr_prescricao_p
	and	nr_sequencia = nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
