CREATE OR REPLACE PROCEDURE sup_vincula_ordem_requisicao ( nr_requisicao_p bigint, nr_ordem_servico_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w	bigint;
nr_ordem_ant_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	requisicao_material
where	nr_requisicao = nr_requisicao_p
and	(nr_seq_ordem_serv IS NOT NULL AND nr_seq_ordem_serv::text <> '');

if (qt_existe_w > 0) then
	begin

	select	nr_seq_ordem_serv
	into STRICT	nr_ordem_ant_w
	from	requisicao_material
	where	nr_requisicao = nr_requisicao_p;
	end;
end if;

update	requisicao_material
set	nr_seq_ordem_serv = nr_ordem_servico_p,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_requisicao = nr_requisicao_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
