SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sup_virada_saldo_projetos ( cd_estabelecimento_p bigint, nm_usuario_p text, cd_perfil_p bigint) AS $body$
DECLARE

 
nr_seq_projeto_w		projeto_recurso.nr_sequencia%type;
nr_sequencia_w			projeto_recurso.nr_sequencia%type;
dt_ultimo_mes_w			timestamp;
dt_mes_w			timestamp;
dt_inicio_projeto_w		timestamp;
qt_registro_w			bigint;

c01 CURSOR FOR 
SELECT	a.nr_sequencia, 
	trunc(coalesce(a.dt_inicio_exec,clock_timestamp()),'mm') 
from	projeto_recurso a 
where	coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p;

c02 CURSOR FOR 
SELECT	dt_mes 
from	mes_v 
where	dt_mes >= dt_ultimo_mes_w	 
order by dt_mes;


BEGIN 
 
CALL wheb_usuario_pck.set_cd_estabelecimento(cd_estabelecimento_p);
CALL wheb_usuario_pck.set_nm_usuario(nm_usuario_p);
CALL wheb_usuario_pck.set_cd_perfil(cd_perfil_p);
 
open C01;
loop 
fetch C01 into	 
	nr_seq_projeto_w, 
	dt_inicio_projeto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	trunc(max(dt_mesano_referencia),'mm') 
	into STRICT	dt_ultimo_mes_w 
	from	projeto_recurso_saldo 
	where	nr_seq_projeto = nr_seq_projeto_w;
	 
	select	count(*) 
	into STRICT	qt_registro_w 
	from	projeto_recurso_saldo 
	where	nr_seq_projeto = nr_seq_projeto_w;
	 
	 
	if (coalesce(dt_ultimo_mes_w::text, '') = '') then 
		dt_ultimo_mes_w := dt_inicio_projeto_w;
	end if;
	 
	if (dt_ultimo_mes_w = trunc(clock_timestamp(),'mm')) and (qt_registro_w > 0) then 
	 
		select	coalesce(max(nr_sequencia),0) 
		into STRICT	nr_sequencia_w 
		from	projeto_recurso_saldo 
		where	nr_seq_projeto = nr_seq_projeto_w 
		and	dt_mesano_referencia = dt_ultimo_mes_w;
		 
		if (nr_sequencia_w > 0) then 
			CALL sup_atualizar_saldo_projeto(nr_sequencia_w, nr_seq_projeto_w, cd_estabelecimento_p, nm_usuario_p);
		end if;
	else 
	 
		open C02;
		loop 
		fetch C02 into	 
			dt_mes_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin 
			CALL sup_gerar_saldo_projeto(nr_seq_projeto_w, dt_mes_w, cd_estabelecimento_p, nm_usuario_p);
			end;
		end loop;
		close C02;
	end if;	
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
