SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE suspender_ciclos_paciente ( cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


nr_prescricao_w		bigint;
nr_seq_atendimento_w 	bigint;
				
C01 CURSOR FOR
	SELECT	a.nr_prescricao,
		a.nr_seq_atendimento
	from	paciente_setor b,
		paciente_atendimento a
	where	a.nr_seq_paciente = b.nr_seq_paciente
	and 	b.cd_pessoa_fisica = cd_pessoa_fisica_p
	and 	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_prevista) >= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp());
	

BEGIN

open C01;
loop
fetch C01 into	
	nr_prescricao_w,
	nr_seq_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	prescr_medica
	set 	dt_suspensao = clock_timestamp(),
		nm_usuario_susp = nm_usuario_p
	where 	nr_prescricao = nr_prescricao_w
	and 	not exists (	SELECT 1 
				from	prescr_material 
				where 	nr_prescricao = nr_prescricao_w 
				and	cd_motivo_baixa <> 0);
	
	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
	
	update	paciente_atendimento
	set 	dt_suspensao = clock_timestamp(),
		dt_cancelamento = clock_timestamp(),
		nm_usuario_suspensao =  nm_usuario_p
	where 	nr_seq_atendimento = nr_seq_atendimento_w;
	
	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
	
	end;
end loop;
close C01;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
