SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE suspender_ciclo_cih (nr_prescricao_p bigint, nr_seq_material_p bigint) AS $body$
DECLARE


nr_prescricao_w			prescr_medica.nr_prescricao%type;
cd_material_w			prescr_material.cd_material%type;
qt_dias_solicitado_w	prescr_material.qt_dias_solicitado%type;
qt_total_dias_lib_w		prescr_material.qt_total_dias_lib%type;	
cd_estabelecimento_w	bigint;
dt_prescricao_w			prescr_medica.dt_prescricao%type;
dt_posterior_w			prescr_medica.dt_prescricao%type;
nr_atendimento_w		prescr_medica.nr_atendimento%type;
cd_pessoa_fisica_w		prescr_medica.cd_pessoa_fisica%type;
ie_atb_pessoa_w			varchar(1);
ie_dias_util_medic_w	material.ie_dias_util_medic%type;
nr_dia_util_w			prescr_material.nr_dia_util%type;	
max_nr_dia_util_w		prescr_material.nr_dia_util%type;	
ie_existe_w				boolean := false;


c01 CURSOR FOR
SELECT	a.nr_prescricao
from 	prescr_material a,
	prescr_medica b
where 	a.nr_prescricao	= b.nr_prescricao
and	a.nr_prescricao <> nr_prescricao_p
and	a.ie_suspenso	<> 'S'
and	((b.dt_inicio_prescr between dt_prescricao_w and dt_posterior_w) or (b.dt_prescricao between dt_prescricao_w and dt_posterior_w))
and 	coalesce(ie_ciclo_reprov,'N') = 'N'	
and	coalesce(ie_atb_pessoa_w,'N') = 'N'
and	b.nr_atendimento	= nr_atendimento_w
and	a.cd_material  	= cd_material_w

union all

SELECT	a.nr_prescricao
from 	prescr_material a,
	prescr_medica b
where 	a.nr_prescricao  = b.nr_prescricao
and	b.nr_prescricao	  <> nr_prescricao_p
and 	a.ie_suspenso	  <> 'S'
and	((b.dt_inicio_prescr between dt_prescricao_w and dt_posterior_w) or (b.dt_prescricao between dt_prescricao_w and dt_posterior_w))
and 	coalesce(ie_ciclo_reprov,'N') = 'N'	
and	coalesce(ie_atb_pessoa_w,'N') = 'S'
and 	b.cd_pessoa_fisica	   = cd_pessoa_fisica_w
and	a.cd_material 	   = cd_material_w
order by 1;


BEGIN

cd_estabelecimento_w	:= coalesce(wheb_usuario_pck.get_cd_estabelecimento,0);

select	max(cd_material),
	max(qt_dias_solicitado),
	max(nr_dia_util),
	max(qt_total_dias_lib)
into STRICT 	cd_material_w,
	qt_dias_solicitado_w,
	nr_dia_util_w,
	qt_total_dias_lib_w
from  	prescr_material                                                                                                                                                                                                                               
where 	nr_sequencia 	= nr_seq_material_p                                                                                                                                                                                                           
and	nr_prescricao 	    = nr_prescricao_p;

select max(dt_prescricao),
	max(nr_atendimento),
	max(cd_pessoa_fisica)
into STRICT dt_prescricao_w,
	nr_atendimento_w,
	cd_pessoa_fisica_w
from   	prescr_medica
where  	nr_prescricao = nr_prescricao_p;


select 	coalesce(max(ie_dias_util_medic),'N')
into STRICT	ie_dias_util_medic_w
from	Material
where	cd_material = cd_material_w;


select	coalesce(max(ie_atb_pessoa),'N')
into STRICT	ie_atb_pessoa_w
from	parametro_medico
where	cd_estabelecimento	= cd_estabelecimento_w;

dt_posterior_w	:= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_prescricao_w) + qt_dias_solicitado_w;

select	max(a.nr_dia_util)
into STRICT	max_nr_dia_util_w
from 	prescr_material a,
	prescr_medica b
where 	a.nr_prescricao	= b.nr_prescricao
and	a.nr_prescricao <> nr_prescricao_p
and	a.ie_suspenso	<> 'S'
and	((b.dt_inicio_prescr between dt_prescricao_w and dt_posterior_w) or (b.dt_prescricao between dt_prescricao_w and dt_posterior_w))
and 	coalesce(ie_ciclo_reprov,'N') = 'N'	
and	((coalesce(ie_atb_pessoa_w,'N') = 'N' and b.nr_atendimento	= nr_atendimento_w) or (coalesce(ie_atb_pessoa_w,'N') = 'S' and b.cd_pessoa_fisica	= cd_pessoa_fisica_w))
order by 1;

if	((coalesce(qt_dias_solicitado_w,0) > 0) and
	((coalesce(qt_total_dias_lib_w,0) = 0) or (coalesce(nr_dia_util_w,0) >  coalesce(qt_total_dias_lib_w,0))) and
	((ie_dias_util_medic_w = 'O' and nr_dia_util_w = 0) or (ie_dias_util_medic_w = 'S' and nr_dia_util_w = 1))) then
	open C01;
		loop
		fetch C01 into	
		nr_prescricao_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
		update 	prescr_material
		set	   qt_total_dias_lib      = max_nr_dia_util_w
		where  nr_prescricao          = nr_prescricao_w
		and	   cd_material            = cd_material_w;
		ie_existe_w	:= true; 	
		end loop;
		close C01;

		if (ie_existe_w) then
			update 	prescr_material
			set	   qt_total_dias_lib      = max_nr_dia_util_w
			where  nr_prescricao          = nr_prescricao_p
			and	   nr_sequencia		      = nr_seq_material_p;
		end if;
		commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE suspender_ciclo_cih (nr_prescricao_p bigint, nr_seq_material_p bigint) FROM PUBLIC;

