CREATE OR REPLACE PROCEDURE suspender_ciclo_trat_onco ( nr_seq_paciente_p bigint, nr_ciclo_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_atendimento_w	bigint;
nr_prescricao_w		bigint;
ie_suspender_prescricao_w	varchar(3) := 'S';
			
c01 CURSOR FOR 
	SELECT	nr_seq_atendimento, 
		nr_prescricao 
	into STRICT	nr_seq_atendimento_w, 
		nr_prescricao_w 
	from	paciente_atendimento 
	where	nr_seq_paciente = nr_seq_paciente_p 
	and	nr_ciclo = nr_ciclo_p 
	and	trunc(coalesce(dt_real,clock_timestamp())) >= trunc(clock_timestamp()) 
	and	coalesce(substr(Qt_Obter_Status_Agenda(nr_seq_atendimento),1,50),'XPTO')	<> 'F' 
	and	((coalesce(ie_suspender_prescricao_w,'X') in ('S','T','X')) or	 
		((coalesce(ie_suspender_prescricao_w,'X') in ('D','P')) and (coalesce(nr_prescricao::text, '') = '')));
		

BEGIN 
 
ie_suspender_prescricao_w := Obter_Param_Usuario(281, 750, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_suspender_prescricao_w);
 
open c01;
loop 
fetch c01 into 
	nr_seq_atendimento_w, 
	nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	CALL suspender_dia_tratamento_onco(nr_prescricao_w, nr_seq_atendimento_w, nm_usuario_p);
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
