SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE suspender_dia_trat_onco_alta ( nr_prescricao_p bigint, nr_seq_atendimento_p bigint, nm_usuario_p text, ie_obito_p text default 'N') AS $body$
DECLARE



nr_seq_ordem_w			bigint;
dt_agenda_w			timestamp;
	
nr_seq_medic_w			bigint;
cd_estabelecimento_w		smallint;
ie_permite_cancelar_w		varchar(1);

nr_seq_agenda_cons_w		paciente_atendimento.nr_seq_agenda_cons%type;
nr_seq_agequi_w			bigint;
ie_cancelar_autor_conv_w	varchar(15);
ie_status_agenda_w		varchar(3);
ie_Status_agenda_quimi_w	varchar(3);
nr_seq_paciente_setor_w		bigint;
nr_ciclo_w			smallint;
ds_retorno_receita_w		varchar(4000);
nr_seq_paciente_w		bigint;
nr_seq_ageint_item_w	bigint;
nr_seq_pendencia_w	bigint;

C02 CURSOR FOR
	SELECT 	a.nr_seq_interno
	from	paciente_atendimento b,
		paciente_atend_medic a
	where	a.nr_seq_atendimento	= b.nr_seq_atendimento
	and	a.nr_seq_atendimento	= nr_seq_atendimento_p;

C01 CURSOR FOR
	SELECT	b.nr_seq_ordem
	from	can_ordem_item_prescr b,
		prescr_material a
	where	a.nr_prescricao		= nr_prescricao_p
	and	nr_seq_atend_medic	= nr_seq_medic_w
	and	a.nr_prescricao		= b.nr_prescricao
	and	a.nr_sequencia		= b.nr_seq_prescricao;


BEGIN

select	max(coalesce(cd_estabelecimento,0))
into STRICT	cd_estabelecimento_w
from	prescr_medica
where	nr_prescricao	=	nr_prescricao_p;

ie_permite_cancelar_w := Obter_Param_Usuario(281, 282, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_permite_cancelar_w);
ie_cancelar_autor_conv_w := Obter_Param_Usuario(281, 1101, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_cancelar_autor_conv_w);
ie_Status_agenda_quimi_w := Obter_Param_Usuario(281, 1199, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_Status_agenda_quimi_w);

select	trunc(coalesce(max(a.dt_real),max(a.dt_prevista))),
	max(nr_seq_paciente),
	max(nr_ciclo)
into STRICT	dt_agenda_w,
	nr_seq_paciente_setor_w,
	nr_ciclo_w
from	paciente_atendimento a
where	a.nr_seq_atendimento	= nr_seq_atendimento_p;

if (dt_agenda_w < trunc(clock_timestamp())) and (ie_permite_cancelar_w = 'N') and (coalesce(ie_obito_p,'N') = 'N')then
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(191835);
end if;


OPEN C02;
LOOP
FETCH C02 into
	nr_seq_medic_w;
EXIT WHEN NOT FOUND; /* apply on C02 */

	update	paciente_atend_medic
	set	ie_cancelada		= 'S',
		dt_cancelamento		= clock_timestamp(),
		nm_usuario_cancel	= nm_usuario_p
	where	nr_seq_interno		= nr_seq_medic_w;

	update	prescr_material
	set	ie_suspenso		= 'S',
		dt_suspensao		= clock_timestamp(),
		nm_usuario_susp		= nm_usuario_p
	where	nr_prescricao		= nr_prescricao_p
	and	nr_seq_atend_medic	= nr_seq_medic_w;

	OPEN C01;
	LOOP
	FETCH C01 into
		nr_seq_ordem_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		update	can_ordem_prod
		set	ie_cancelada	= 'S',
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_ordem_w;
	END LOOP;
	CLOSE C01;		

END LOOP;
CLOSE C02;


update	paciente_atendimento
set	dt_suspensao		= clock_timestamp(),
	nm_usuario_suspensao	= nm_usuario_p
where	nr_seq_atendimento	= nr_seq_atendimento_p;

ds_retorno_receita_w := Suspender_receita_trat_onc(nr_seq_paciente_setor_w, nr_ciclo_w, nm_usuario_p, ds_retorno_receita_w);

select	max(nr_sequencia),
		max(ie_status_agenda),
		coalesce(max(nr_seq_Ageint_item),0)
into STRICT	nr_seq_agequi_w,
		ie_status_agenda_w,
		nr_seq_ageint_item_w
from	agenda_quimio
where	nr_seq_atendimento	= nr_seq_atendimento_p;

if (ie_status_agenda_w	<> 'C') and (nr_seq_agequi_w	> 0) then
	if (coalesce(ie_obito_p, 'N') = 'S') then
		CALL qt_alterar_status_agenda(nr_seq_agequi_w, 'C', nm_usuario_p, 'N', null, null, cd_estabelecimento_w,'');
	else
		CALL qt_alterar_status_agenda(nr_seq_agequi_w, ie_Status_agenda_quimi_w, nm_usuario_p, 'N', null, null, cd_estabelecimento_w,'');
	end if;
end if;

select	max(nr_seq_agenda_cons),
	coalesce(max(nr_seq_pend_Agenda),0)
into STRICT	nr_seq_agenda_cons_w,
	nr_seq_pendencia_w
from	paciente_atendimento
where	nr_seq_atendimento = nr_seq_atendimento_p;

if (nr_seq_ageint_item_w	> 0) then
	begin
		delete	FROM agenda_quimio_marcacao
		where	nr_seq_ageint_item	= nr_seq_ageint_item_w;
	exception
	when others then
		nr_seq_ageint_item_w := coalesce(nr_seq_ageint_item_w, 0);
	end;
elsif (nr_seq_pendencia_w	> 0) then		
	begin
		delete	FROM agenda_quimio_marcacao
		where	nr_seq_pend_agenda	= nr_seq_pendencia_w
		and	trunc(dt_Agenda)	= trunc(dt_Agenda_w);
		
		delete FROM w_agenda_quimio
		where ie_status = 'AG' 
		and nr_seq_atendimento = nr_Seq_atendimento_p;
	exception
	when others then
		nr_seq_ageint_item_w := coalesce(nr_seq_ageint_item_w, 0);
	end;
end if;


if (nr_seq_agenda_cons_w IS NOT NULL AND nr_seq_agenda_cons_w::text <> '') then
	CALL Cancelar_Agenda_Consulta(nr_seq_agenda_cons_w, nm_usuario_p);
end if;

if (coalesce(ie_cancelar_autor_conv_w,'N') = 'S') then
	update	autorizacao_convenio
	set	nr_seq_estagio	=(	SELECT	max(x.nr_sequencia)
					from	estagio_autorizacao x
					where	x.ie_interno = '70'
          				and	OBTER_EMPRESA_ESTAB(wheb_usuario_pck.get_cd_estabelecimento) = x.cd_empresa),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao 	= clock_timestamp()
	where	((nr_seq_paciente = nr_seq_atendimento_p) or
		((coalesce(nr_seq_paciente::text, '') = '') and (nr_seq_paciente_setor = nr_seq_paciente_setor_w) and (nr_ciclo = nr_ciclo_w)));
end if;



END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE suspender_dia_trat_onco_alta ( nr_prescricao_p bigint, nr_seq_atendimento_p bigint, nm_usuario_p text, ie_obito_p text default 'N') FROM PUBLIC;

