CREATE OR REPLACE PROCEDURE suspender_etapas_sne_js ( ds_sequencia_hor_p text, nr_seq_motivo_p bigint, ds_justificativa_p text, nm_usuario_p text) AS $body$
DECLARE

 
ds_horarios_w	varchar(4000) := '';
nr_sequencia_w	bigint;
k		integer;
					

BEGIN 
 
ds_horarios_w := ds_sequencia_hor_p;
 
while	(ds_horarios_w IS NOT NULL AND ds_horarios_w::text <> '') loop 
	begin 
	select	position(',' in ds_horarios_w) 
	into STRICT	k 
	;
 
	if (k > 1) and ((substr(ds_horarios_w, 1, k -1) IS NOT NULL AND (substr(ds_horarios_w, 1, k -1))::text <> '')) then 
		nr_sequencia_w		:= substr(ds_horarios_w, 1, k-1);
		nr_sequencia_w		:= replace(nr_sequencia_w, ',','');
		ds_horarios_w	:= substr(ds_horarios_w, k + 1, 4000);
	elsif (ds_horarios_w IS NOT NULL AND ds_horarios_w::text <> '') then 
		nr_sequencia_w		:= replace(ds_horarios_w,',','');
		ds_horarios_w	:= '';
	end if;
	 
	CALL suspender_etapa_sne(nr_sequencia_w, nm_usuario_p, nr_seq_motivo_p, ds_justificativa_p);
	 
	end;
end loop;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
