CREATE OR REPLACE PROCEDURE suspender_etapa_hemoterapia ( nr_seq_item_p bigint, nr_prescricao_p bigint, nr_prescricoes_p text, nr_seq_horario_p bigint, nm_usuario_p text) AS $body$
DECLARE

nr_prescricao_w			bigint;
nr_seq_horario_w		bigint;
nr_seq_procedimento_w	integer;
dt_horario_w			timestamp;
nr_etapa_w				integer;
nr_sequencia_w			bigint;
c01 CURSOR FOR
SELECT	a.nr_sequencia,
		a.nr_prescricao,
		b.nr_sequencia,
		b.dt_horario,
		b.nr_etapa
from		prescr_proc_hor			b,
		prescr_procedimento		a
where	a.nr_prescricao = b.nr_prescricao
and		a.nr_sequencia = b.nr_seq_procedimento
and		((a.nr_prescricao = nr_prescricao_p AND a.nr_sequencia = nr_seq_item_p)or
		 obter_se_contido(a.nr_prescricao,nr_prescricoes_p) = 'S')
and		b.nr_sequencia = nr_seq_horario_p
and 		coalesce(b.dt_inicio_horario::text, '') = ''
and		coalesce(b.dt_suspensao::text, '') = ''
and 		coalesce(b.dt_fim_horario::text, '') = ''
and (coalesce(b.ie_horario_especial,'N') = 'N');

BEGIN

if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') and (nr_seq_horario_p > 0) then
	open c01;
	loop
	fetch c01 into	nr_seq_procedimento_w,
				nr_prescricao_w,
				nr_seq_horario_w,
				dt_horario_w,
				nr_etapa_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		
		update	prescr_proc_hor
		set		dt_suspensao		= clock_timestamp(),
				nm_usuario_susp 	= nm_usuario_p
		where	nr_sequencia 		= nr_seq_horario_w
		and		nr_prescricao 		= nr_prescricao_w
		and		nr_seq_procedimento = nr_seq_procedimento_w;

		select	nextval('prescr_solucao_evento_seq')
		into STRICT	nr_sequencia_w
		;

		insert into prescr_solucao_evento(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_procedimento,
			cd_pessoa_fisica,
			ie_alteracao,
			dt_alteracao,
			ie_evento_valido,
			ie_tipo_solucao,
			nr_etapa_evento,
			nr_prescricao,
            dt_horario)
		values (
			nr_sequencia_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_procedimento_w,
			obter_dados_usuario_opcao(nm_usuario_p, 'C'),
			12,
			clock_timestamp(), 
			'S',
			3,
			coalesce(nr_etapa_w,0),
			nr_prescricao_w,
            dt_horario_w);
		end;
	end loop;
	close c01;
	commit;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
