CREATE OR REPLACE PROCEDURE suspender_horarios_extensao (nr_atendimento_p bigint, nr_prescr_nova_p bigint, dt_inicio_prescr_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
				    
 
nr_prescricao_w			bigint;

 
c01 CURSOR FOR 
	SELECT	distinct nr_prescricao 
	from	prescr_medica 
	where	nr_prescricao	<> nr_prescr_nova_p 
	and	nr_atendimento 	= nr_atendimento_p 
	and	coalesce(dt_suspensao::text, '') = '' 
	and	dt_validade_prescr > dt_inicio_prescr_p 
	and	cd_setor_atendimento <> Obter_Setor_Atendimento(nr_atendimento_p);

BEGIN
 
 
 
open C01;
	loop 
	fetch C01 into	 
		nr_prescricao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		CALL Suspender_Prescricao(nr_prescricao_w,null,substr(/*'Suspensão automática conforme parâmetro[162] do Plano Terapêutico!'*/
 obter_desc_expressao(782158),1,255),nm_usuario_p, 'N');
		--Suspender_prescr_mat_hor(nr_sequencia_w, nm_usuario_p, 0, null, null,substr(/*'Suspensão automática conforme parâmetro[162] do Plano Terapêutico!'*/ obter_desc_expressao(782158),1,255)); 
		end;
	end loop;
	close C01;
 
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
