CREATE OR REPLACE PROCEDURE suspender_horario_proc (nr_prescricao_p bigint, nr_seq_medic_p bigint, nr_atendimento_p bigint, cd_proced_p bigint, nr_seq_horarios_p text, dt_horario_p timestamp, nm_usuario_p text) AS $body$
DECLARE

			   
nr_seq_horarios_w	varchar(255);
nr_pos_virgula_w 	smallint;	
nr_seq_horario_w	bigint;	
ie_ctrl_glic_w		varchar(15);
ie_tipo_item_w		varchar(10);

seqTeste varchar(200) := '';


BEGIN 
nr_seq_horarios_w := null;
if (nr_seq_horarios_p IS NOT NULL AND nr_seq_horarios_p::text <> '') then 
	begin 
	nr_seq_horarios_w := trim(both nr_seq_horarios_p);
	while(nr_seq_horarios_w IS NOT NULL AND nr_seq_horarios_w::text <> '') loop 
		begin 
		nr_pos_virgula_w := position(',' in nr_seq_horarios_w);		
		if (nr_pos_virgula_w > 0) then 
			begin 
			nr_seq_horario_w	:= (substr(nr_seq_horarios_w,1,nr_pos_virgula_w-1))::numeric;
			nr_seq_horarios_w	:= substr(nr_seq_horarios_w,nr_pos_virgula_w+1,length(nr_seq_horarios_w));			
			end;
		else 
			begin 
			nr_seq_horario_w	:= (nr_seq_horarios_w)::numeric;
			nr_seq_horarios_w	:= null;
			end;
		end if;	
 
		select	obter_ctrl_glic_proc(nr_seq_proc_interno) 
		into STRICT	ie_ctrl_glic_w 
		from	prescr_proc_hor 
		where	nr_sequencia = nr_seq_horario_w;
 
		if (ie_ctrl_glic_w = 'CCG') then 
			ie_tipo_item_w	:= 'G';		
		elsif (ie_ctrl_glic_w = 'CIG') then 
			ie_tipo_item_w	:= 'C';		
		else 
			ie_tipo_item_w	:= 'P';
		end if;
		 
		-- suspender hor√°rio 
		CALL suspender_prescr_proc_hor(nr_seq_horario_w, nm_usuario_p);
		-- gerar evento ADEP 
		CALL Gerar_Alter_Hor_Prescr_Adep(nr_atendimento_p, ie_tipo_item_w, cd_proced_p, nr_prescricao_p, nr_seq_medic_p, nr_seq_horario_w,dt_horario_p, 12, null, null, null, nm_usuario_p);		
		end;
	end loop;	
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
