CREATE OR REPLACE PROCEDURE suspender_hor_item_prescr_js ( ds_lista_p text, nm_usuario_p text, ie_susp_apos_p text ) AS $body$
DECLARE

 
nr_sequencia_w		integer;
nr_prescricao_w		bigint;
nm_tabela_w		varchar(4000);
nr_pos_virgula_w	bigint;
nr_pos_hifem_w		bigint;
ds_lista_w		varchar(4000);
ds_lista_aux_w		varchar(4000);


BEGIN 
if (ds_lista_p IS NOT NULL AND ds_lista_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	ds_lista_w	:= ds_lista_p;
	 
	while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop 
		begin 
		nr_pos_virgula_w	:= position(',' in ds_lista_w);
		if (nr_pos_virgula_w > 0) then 
			begin 
			ds_lista_aux_w	:= substr(ds_lista_w,0,nr_pos_virgula_w-1);
			ds_lista_w	:= substr(ds_lista_w,nr_pos_virgula_w+1,length(ds_lista_w));			
			end;
		else 
			begin 
			ds_lista_aux_w	:= ds_lista_w;
			ds_lista_w	:= null;
			end;
		end if;	
		 
		if (ds_lista_aux_w IS NOT NULL AND ds_lista_aux_w::text <> '') then 
			begin 
			nr_pos_hifem_w	:= position('-' in ds_lista_aux_w);
			if (nr_pos_hifem_w > 0) then 
				begin 
				nr_prescricao_w	:= substr(ds_lista_aux_w,0,nr_pos_hifem_w-1);
				ds_lista_aux_w	:= substr(ds_lista_aux_w,nr_pos_hifem_w+1,length(ds_lista_aux_w));
				 
				if (ds_lista_aux_w IS NOT NULL AND ds_lista_aux_w::text <> '') then 
					begin 
					nr_pos_hifem_w	:= position('-' in ds_lista_aux_w);
					if (nr_pos_hifem_w > 0) then 
						begin 
						nr_sequencia_w	:= substr(ds_lista_aux_w,0,nr_pos_hifem_w-1);
						ds_lista_aux_w	:= substr(ds_lista_aux_w,nr_pos_hifem_w+1,length(ds_lista_aux_w));
						 
						if (ds_lista_aux_w IS NOT NULL AND ds_lista_aux_w::text <> '') then 
							begin 
							nr_pos_hifem_w	:= position('-' in ds_lista_aux_w);
							if (nr_pos_hifem_w > 0) then 
								begin 
								nm_tabela_w	:= substr(ds_lista_aux_w,0,nr_pos_hifem_w-1);
								ds_lista_aux_w	:= substr(ds_lista_aux_w,nr_pos_hifem_w+1,length(ds_lista_aux_w));
								 
								CALL suspender_hor_item_prescr( 
									nr_prescricao_w, 
									nr_sequencia_w, 
									nm_tabela_w, 
									nm_usuario_p, 
									'S', 
									ie_susp_apos_p);								
								end;
							end if;	
							end;
						end if;
						end;
					end if;	
					end;
				end if;				
				end;
			end if;	
			end;
		end if;
		end;
	end loop;
	end;
end if;
