CREATE OR REPLACE PROCEDURE suspender_hor_sol_hd_orig ( nr_prescricao_p bigint, nm_usuario_p text, dt_inicio_susp_p timestamp default clock_timestamp()) AS $body$
DECLARE

 
nr_seq_horario_w		bigint;
nr_seq_anterior_w		bigint;
nr_prescricao_original_w	bigint;

-- Buscar solução original de diálise 
c00 CURSOR FOR 
SELECT	a.nr_seq_anterior, 
	a.nr_prescricao_original 
from	prescr_solucao a 
where	a.nr_prescricao	= nr_prescricao_p;

-- Buscar horários da solução original de diálise 
C01 CURSOR FOR 
SELECT	c.nr_sequencia 
from	prescr_solucao a, 
	prescr_mat_hor c 
where	a.nr_prescricao = nr_prescricao_original_w 
and	a.nr_prescricao	 = c.nr_prescricao 
and	a.nr_seq_solucao = nr_seq_anterior_w 
and	a.nr_seq_solucao = c.nr_seq_solucao 
and	(a.nr_seq_dialise IS NOT NULL AND a.nr_seq_dialise::text <> '') 
and	coalesce(a.ie_hemodialise,'N') <> 'N' 
and	((coalesce(dt_inicio_susp_p::text, '') = '') or (c.dt_horario >= dt_inicio_susp_p)) 
and	coalesce(c.dt_fim_horario::text, '') = '' 
and	coalesce(c.dt_suspensao::text, '') = '';
	

BEGIN 
 
open C00;
loop 
fetch C00 into	 
	nr_seq_anterior_w, 
	nr_prescricao_original_w;
EXIT WHEN NOT FOUND; /* apply on C00 */
	 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_horario_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		CALL suspender_prescr_mat_hor(nr_seq_horario_w, nm_usuario_p, 0, null, null,null);
	end loop;
	close C01;
 
end loop;
close C00;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
