CREATE OR REPLACE PROCEDURE suspender_item_adep_processo ( nr_seq_item_p bigint, ie_suspender_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') and (ie_suspender_p IS NOT NULL AND ie_suspender_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	if (ie_suspender_p = 'S') then
		update	adep_processo_item
		set		ie_suspenso = 'S'
		where	obter_se_susp_item_processo(nr_seq_processo) = 'S'
		and		coalesce(ie_suspenso,'N') = 'N'
		and		nr_sequencia = nr_seq_item_p;
	else
		update	adep_processo_item
		set		ie_suspenso = 'N'
		where	ie_suspenso	= 'S'
		and		nr_sequencia = nr_seq_item_p;
	end if;
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
