CREATE OR REPLACE PROCEDURE suspender_item_prescricao_gf ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_tipo_p text, cd_motivo_p bigint, ds_motivo_p text, cd_perfil_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint ) AS $body$
DECLARE

 
nm_tabela_w		varchar(100);			
VarIEExame_w		varchar(15);
ie_consistencia_w	varchar(5);
			

BEGIN 
 
VarIEExame_w := Obter_Param_Usuario(924, 91, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, VarIEExame_w);
 
if (ie_tipo_p = 'M') OR (ie_tipo_p = 'S') OR (ie_tipo_p = '2') then 
	nm_tabela_w := 'PRESCR_MATERIAL';
elsif (ie_tipo_p = 'D') then 
	nm_tabela_w := 'PRESCR_DIETA';
elsif (ie_tipo_p = 'R') then 
	nm_tabela_w := 'PRESCR_RECOMENDACAO';
elsif (ie_tipo_p = 'P') then 
	nm_tabela_w := 'PRESCR_PROCEDIMENTO';
elsif (ie_tipo_p = 'G') then 
	nm_tabela_w := 'PRESCR_GASOTERAPIA';
elsif (ie_tipo_p = '1') then 
	nm_tabela_w := 'PRESCR_SOLUCAO';
elsif (ie_tipo_p = '4') then 
	nm_tabela_w := 'NUT_PACIENTE';
elsif (ie_tipo_p = '5') then 
	nm_tabela_w := 'NUT_PAC';
end if;
ie_consistencia_w := Consiste_supensao_prescr(nr_prescricao_p, nr_sequencia_p, nm_tabela_w, VarIEExame_w, cd_estabelecimento_p, ie_consistencia_w, nm_usuario_p, Obter_perfil_Ativo);
 
CALL Suspender_item_Prescricao(nr_prescricao_p,nr_sequencia_p,cd_motivo_p,ds_motivo_p,nm_tabela_w,nm_usuario_p,'S',7010);
 
END;
