CREATE OR REPLACE PROCEDURE suspender_medic_npt ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_tipo_item_p text, nm_usuario_p text) AS $body$
DECLARE



cd_material_w				prescr_material.cd_material%type;
nr_sequencia_w				prescr_material.nr_sequencia%type;


c01 CURSOR FOR
	SELECT	cd_material,
			nr_sequencia
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_p
	and		nr_seq_nut_pac		= nr_sequencia_p
	and		ie_tipo_item_p		= 'NAN'
	and		coalesce(dt_suspensao::text, '') = '';


BEGIN

open c01;
loop
	fetch c01 into
		cd_material_w,
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	update	prescr_material
	set		ie_suspenso		= 'S',
			dt_suspensao	= clock_timestamp(),
			dt_atualizacao  = clock_timestamp(),
			nm_usuario_susp	= nm_usuario_p
	where	nr_prescricao	= nr_prescricao_p
	and		nr_sequencia	= nr_sequencia_w
	and		cd_material		= cd_material_w;

	update	prescr_mat_hor
	set		dt_suspensao	= clock_timestamp(),
			nm_usuario_susp	= nm_usuario_p
	where	nr_seq_material = nr_sequencia_w
	and		nr_prescricao	= nr_prescricao_p
	and		cd_material		= cd_material_w
	and		coalesce(dt_fim_horario::text, '') = ''
	and		coalesce(dt_suspensao::text, '') = '';

end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
