CREATE OR REPLACE PROCEDURE suspender_prescricao_sae ( nr_sequencia_p bigint, cd_motivo_p bigint, ds_motivo_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_prescricao_w		bigint;
nr_sequencia_w		bigint;
nr_seq_proc_w		bigint;
nr_atendimento_w	bigint;

C01 CURSOR FOR 
	SELECT	nr_sequencia, 
			nr_seq_proc 
	from	pe_prescr_proc 
	where	nr_seq_prescr	= nr_sequencia_p;
	

BEGIN 
 
select	max(nr_prescricao), 
		coalesce(max(nr_atendimento), obter_atendimento_prescr(max(nr_prescricao))) 
into STRICT	nr_prescricao_w, 
		nr_atendimento_w 
from	pe_prescricao 
where	nr_sequencia	= nr_sequencia_p;
 
 
update	pe_prescricao 
set		dt_suspensao = clock_timestamp(), 
		nm_usuario_susp = nm_usuario_p, 
		nr_seq_motivo_susp	= cd_motivo_p 
where	nr_sequencia	= nr_sequencia_p;
 
if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then 
	begin 
	CALL Suspender_Prescricao(	nr_prescricao_w, 
							cd_motivo_p, 
							ds_motivo_p, 
							nm_usuario_p, 
							'N');
	end;
end if;
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w, 
	nr_seq_proc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL Suspender_item_SAE(	nr_sequencia_w, 
				cd_motivo_p, 
				ds_motivo_p, 
				nm_usuario_p);
	CALL gerar_alter_item_prescr_adep(nr_atendimento_w, 'E', nr_seq_proc_w, nr_sequencia_p, nr_sequencia_w, 14, ds_motivo_p, nm_usuario_p, cd_motivo_p,null);
	end;
end loop;
close C01;
 
if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
