CREATE OR REPLACE PROCEDURE suspender_prescr_lib_medic_alt (nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_perfil_w	integer;
nr_seq_susp_prescr_alta_w	varchar(10);
nr_prescricao_w	bigint;

C01 CURSOR FOR
SELECT	nr_prescricao
from	prescr_medica
where	nr_atendimento = nr_atendimento_p
and	coalesce(DT_LIBERACAO::text, '') = ''
and	(DT_LIBERACAO_MEDICO IS NOT NULL AND DT_LIBERACAO_MEDICO::text <> '')
and	coalesce(dt_suspensao::text, '') = '';


BEGIN
cd_perfil_w := wheb_usuario_pck.get_cd_perfil;
nr_seq_susp_prescr_alta_w := Obter_Param_Usuario(924, 505, cd_perfil_w, nm_usuario_p, 0, nr_seq_susp_prescr_alta_w);
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_seq_susp_prescr_alta_w IS NOT NULL AND nr_seq_susp_prescr_alta_w::text <> '') then
	open C01;
	loop
	fetch C01 into
		nr_prescricao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		CALL Suspender_Prescricao(nr_prescricao_w, nr_seq_susp_prescr_alta_w, Obter_desc_expressao(729632), nm_usuario_p, 'N');
		end;
	end loop;
	close C01;
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
