CREATE OR REPLACE PROCEDURE suspender_prescr_rec_hor ( nr_sequencia_p bigint, nm_usuario_p text, ie_prescr_alteracao_p text default 'N') AS $body$
DECLARE


cd_recomendacao_w		prescr_rec_hor.cd_recomendacao%type;
nr_seq_alteracao_w		prescr_mat_alteracao.nr_sequencia%type;
nr_atendimento_w		atendimento_paciente.nr_atendimento%type;
ds_justificativa_w		prescr_mat_alteracao.ds_justificativa%type;
nr_prescricao_w			bigint;
nr_seq_recomendacao_w	integer;
ie_gravar_justif_w		varchar(1);
dt_horario_w			timestamp;


BEGIN

select	max(nr_seq_recomendacao),
	max(nr_prescricao),
	max(cd_recomendacao),
	max(dt_horario),
	max(CASE WHEN ie_gravar_justif_w='S' THEN  PERFORM obter_desc_expressao(727956) END )
into STRICT	nr_seq_recomendacao_w,
	nr_prescricao_w,
	cd_recomendacao_w,
	dt_horario_w,
	ds_justificativa_w
from	prescr_rec_hor
where	nr_sequencia	= nr_sequencia_p;


select	max(nr_atendimento)
into STRICT	nr_atendimento_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_w;

update	prescr_rec_hor
set	dt_suspensao	= clock_timestamp(),
	nm_usuario_susp	= nm_usuario_p
where	nr_sequencia 	= nr_sequencia_p
and	coalesce(dt_fim_horario::text, '') = ''
and	coalesce(dt_suspensao::text, '') = '';

update	prescr_recomendacao
set	ie_horario_susp	= 'S'
where	nr_prescricao	= nr_prescricao_w
and	nr_sequencia	= nr_seq_recomendacao_w;


ie_gravar_justif_w := Obter_Param_Usuario(924, 890, obter_perfil_ativo, nm_usuario_p, 0, ie_gravar_justif_w);

if ((nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and ie_prescr_alteracao_p = 'S') then
	CALL gerar_alter_hor_prescr_adep( nr_atendimento_w, 'R', cd_recomendacao_w,
						nr_prescricao_w, nr_seq_recomendacao_w, nr_sequencia_p,
						dt_horario_w, 5, ds_justificativa_w, null, null, nm_usuario_p);
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
