CREATE OR REPLACE PROCEDURE suspender_proced_cirurgia ( nr_seq_interno_p bigint, nm_usuario_p text ) AS $body$
BEGIN

if (coalesce(nr_seq_interno_p,0) > 0) then
	update	prescr_procedimento
	set	ie_suspenso			= 'S',
		nm_usuario			= nm_usuario_p,
		dt_atualizacao			= clock_timestamp(),
		dt_suspensao			= clock_timestamp(),
		nm_usuario_susp			= nm_usuario_p
	where	nr_seq_interno  		= nr_seq_interno_p
	  and	coalesce(nm_usuario_susp,'X') 	<> 'NÃ£o suspender'
	  and	coalesce(ie_suspenso,'N') 		= 'N'
	  and	coalesce(cd_motivo_baixa,0) 		= 0;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
