CREATE OR REPLACE PROCEDURE suspender_solucao_oncologia ( nr_seq_atendimento_p bigint, nr_seq_solucao_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
			 
nr_prescricao_w	bigint;

			 
 

BEGIN 
 
update	paciente_atend_soluc 
set	dt_cancelamento = clock_timestamp(), 
	nm_usuario_cancel = nm_usuario_p, 
	ie_cancelada = 'S' 
where	nr_seq_solucao = nr_seq_solucao_p 
and	nr_seq_atendimento = nr_seq_atendimento_p;
 
 
Select coalesce(max(nr_prescricao),0) 
into STRICT	nr_prescricao_w 
from	paciente_atendimento 
where  NR_SEQ_ATENDIMENTO = nr_seq_atendimento_p;
 
 
if (nr_prescricao_w > 0 ) then 
 
CALL Suspender_item_Prescricao(	nr_prescricao_w, 
				nr_seq_solucao_p, 
				'', 
				'', 
				'PRESCR_SOLUCAO', 
				nm_usuario_p);
				 
end if;
 
commit;
 
end;
$body$
