CREATE OR REPLACE PROCEDURE susp_medic_atend_ciclo_onc ( nr_seq_atendimento_p bigint, nr_seq_material_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_seq_atendimento_w		bigint;
nr_seq_paciente_w		bigint;
nr_seq_material_w		bigint;
cd_material_w			bigint;
nr_seq_interno_w		bigint;

c01 CURSOR FOR 
	SELECT	a.nr_seq_interno 
	from	paciente_atend_medic a, 
		paciente_atendimento b 
	where	b.nr_seq_paciente	= nr_seq_paciente_w 
	and	b.nr_seq_atendimento = a.nr_seq_atendimento 
	and	b.nr_seq_atendimento	>= nr_seq_atendimento_p 
	and	a.cd_material = cd_material_w 
	and	a.nr_seq_material = nr_seq_material_p 
	and	coalesce(b.dt_liberacao_reg::text, '') = '' 
	and	coalesce(a.dt_cancelamento::text, '') = '' 
	and	coalesce(b.dt_cancelamento::text, '') = '' 
	and	Obter_Se_Dia_Ciclo_Autorizado(b.nr_seq_paciente,b.nr_ciclo) = 'N';
					

BEGIN 
 
select	max(nr_seq_paciente) 
into STRICT	nr_seq_paciente_w 
from	paciente_atendimento 
where	nr_seq_atendimento	= nr_seq_atendimento_p;
 
select	max(cd_material) 
into STRICT	cd_material_w 
from	paciente_atend_medic 
where	nr_seq_atendimento	= nr_seq_atendimento_p 
and	nr_seq_material		= NR_SEQ_MATERIAL_p;
 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_interno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin	 
	CALL suspender_item_oncologia(nr_seq_interno_w,nm_usuario_p);
	end;
end loop;
close C01;	
					 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
