SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_acertar_sus_aih_pk () AS $body$
DECLARE


qt_pk_w		smallint;
nm_tabela_w		varchar(50);
nm_integridade_w	varchar(50);
qt_aih_w		bigint;

c01 CURSOR FOR
	SELECT	nm_tabela,
		nm_integridade_referencial
	from integridade_referencial
	where nm_tabela_referencia = 'SUS_AIH';

BEGIN

select count(*)
into STRICT	qt_pk_w
from	user_cons_columns
where	constraint_name = 'SUSAIH_PK';

if (qt_pk_w = 1) then
	open c01;
	loop
	fetch c01 into	nm_tabela_w,
			nm_integridade_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		CALL exec_sql_dinamico(nm_tabela_w, 'alter table ' || nm_tabela_w || ' drop constraint ' || nm_integridade_w);
	end loop;
	close c01;

	CALL exec_sql_dinamico('SUS_AIH', 'alter table sus_aih drop primary key');

	select	count(*)
	into STRICT	qt_aih_w
	from sus_aih
	where ie_identificacao = '7';

	while qt_aih_w > 0 loop
		update sus_aih
		set ie_identificacao = '1'
		where ie_identificacao = '7'  LIMIT 1000;
		commit;

		qt_aih_w := qt_aih_w - 1000;
	end loop;

	CALL tasy_sincronizar_base();
	CALL valida_objetos_sistema();
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE sus_acertar_sus_aih_pk () FROM PUBLIC;

