CREATE OR REPLACE PROCEDURE sus_aih_gerar_diagnostico_cid ( nr_atendimento_p bigint, cd_cid_p text, nm_usuario_p text) AS $body$
DECLARE

			 
ie_tipo_atendimento_w		smallint;
cd_estabelecimento_w		smallint;
cd_perfil_w			integer;
ie_tipo_diagnostico_w		smallint;
cd_medico_w			varchar(10);
qt_diag_doenca_w		integer	:= 0;
ie_classificacao_doenca_w	varchar(1);
dt_diagnostico_w		timestamp;


BEGIN 
 
select	ie_tipo_atendimento, 
	cd_estabelecimento 
into STRICT	ie_tipo_atendimento_w, 
	cd_estabelecimento_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_p;
 
cd_perfil_w	:= obter_perfil_ativo;
ie_tipo_diagnostico_w := Obter_Param_Usuario(916, 83, cd_perfil_w, nm_usuario_p, cd_estabelecimento_w, ie_tipo_diagnostico_w);
 
cd_medico_w	:= obter_medico_para_diag(nr_atendimento_p, ie_tipo_atendimento_w);
 
dt_diagnostico_w	:= clock_timestamp();
 
insert into diagnostico_medico(	 
	nr_atendimento,   
	dt_diagnostico, 
	ie_tipo_diagnostico, 
	cd_medico, 
	dt_atualizacao, 
	nm_usuario, 
	ie_tipo_atendimento) 
values (	nr_atendimento_p,   
	dt_diagnostico_w, 
	coalesce(ie_tipo_diagnostico_w,0), 
	cd_medico_w, 
	clock_timestamp(), 
	nm_usuario_p, 
	ie_tipo_atendimento_w);
 
select	count(*) 
into STRICT	qt_diag_doenca_w 
from	diagnostico_doenca 
where	nr_atendimento	= nr_atendimento_p;
 
if (qt_diag_doenca_w	> 0) then 
	begin 
	ie_classificacao_doenca_w	:= 'S';
	end;
else 
	begin 
	ie_classificacao_doenca_w	:= 'P';
	end;
end if;
 
insert into diagnostico_doenca(	 
	nr_atendimento, 
	dt_diagnostico, 
	cd_doenca, 
	dt_atualizacao, 
	nm_usuario, 
	ie_classificacao_doenca, 
	IE_TIPO_DIAGNOSTICO) 
values (	nr_atendimento_p, 
	dt_diagnostico_w, 
	cd_cid_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	ie_classificacao_doenca_w, 
	2);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
