CREATE OR REPLACE PROCEDURE sus_aih_unif_eup_js ( nr_aih_p bigint, nr_atendimento_p bigint, dt_emissao_p timestamp, cd_proc_real_p bigint, cd_estabelecimento_p bigint, ds_msg_erro_p INOUT text, ds_msg_conf_p INOUT text) AS $body$
DECLARE

 
nr_atend_w	bigint;
ds_msg_erro_w	varchar(255);
ds_msg_conf_w	varchar(255);
ie_continuar_execucao_w	varchar(1) := 'S';				

BEGIN 
 
select 	coalesce(max(nr_atendimento),0) 
into STRICT	nr_atend_w 
from  sus_aih_unif 
where  nr_aih = nr_aih_p 
and   nr_atendimento <> nr_atendimento_p 
and   obter_pessoa_atendimento(nr_atendimento,'C') <> obter_pessoa_atendimento(nr_atendimento_p,'C');
 
if (nr_atend_w > 0) then 
	ie_continuar_execucao_w := 'N';
	ds_msg_erro_w:= obter_texto_dic_objeto(74460, 1, 'ITEM=' || nr_atend_w);
end if;
 
ds_msg_conf_w := sus_consiste_limite_aih(dt_emissao_p,cd_proc_real_p,cd_estabelecimento_p,null,'E');
 
if (ie_continuar_execucao_w = 'S') and (ds_msg_conf_w IS NOT NULL AND ds_msg_conf_w::text <> '') then 
	ds_msg_conf_p := ds_msg_conf_w;
 
end if;
 
ds_msg_erro_p := ds_msg_erro_w;
 
 
commit;
 
end;
