CREATE OR REPLACE PROCEDURE sus_ajustar_convenio_atend ( nr_atendimento_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
cd_categoria_w		varchar(10);
cd_convenio_sus_w	integer;
cd_tipo_acomodacao_w	smallint;
			

BEGIN 
select	max(cd_convenio_sus) 
into STRICT	cd_convenio_sus_w 
from	parametro_faturamento 
where	cd_estabelecimento	= cd_estabelecimento_p;
 
/*update tipo convenio e tipo atendimento*/
 
update	atendimento_paciente 
set	ie_tipo_atendimento	= 1, 
	ie_tipo_convenio	= 3 
where	nr_atendimento		= nr_atendimento_p;
 
select	max(cd_categoria), 
	max(cd_tipo_acomodacao) 
into STRICT	cd_categoria_w, 
	cd_tipo_acomodacao_w 
from	categoria_convenio 
where	cd_convenio	= cd_convenio_sus_w;
 
/*insert atend_categoria_convenio*/
 
insert into atend_categoria_convenio(nr_seq_interno, 
	nr_atendimento, 
	cd_convenio, 
	cd_categoria, 
	dt_inicio_vigencia, 
	dt_atualizacao, 
	nm_usuario, 
	cd_tipo_acomodacao) 
values (nextval('atend_categoria_convenio_seq'), 
	nr_atendimento_p, 
	cd_convenio_sus_w, 
	cd_categoria_w, 
	clock_timestamp(), 
	clock_timestamp(), 
	nm_usuario_p, 
	cd_tipo_acomodacao_w);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
