CREATE OR REPLACE PROCEDURE sus_ajustar_incon_laudo_base () AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_inconsistencia_w	smallint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_inconsistencia
	from	sus_inconsistencia_laudo
	order by cd_inconsistencia;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	cd_inconsistencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL exec_sql_dinamico('gfsantos',	'update sus_inconsistencia_laudo '||
					' set 	ds_inconsistencia = nvl((select max(ds_inconsistencia) '||
									' from tasy_versao.sus_inconsistencia_laudo '||
									' where cd_inconsistencia = '|| cd_inconsistencia_w ||'),ds_inconsistencia) ,' ||
					'	ds_acao_usuario =  nvl((select max(ds_acao_usuario) '||
									' from tasy_versao.sus_inconsistencia_laudo '||
									' where cd_inconsistencia = '|| cd_inconsistencia_w ||'),ds_acao_usuario) '||
					'where nr_sequencia = '|| nr_sequencia_w );
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
