SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_ajustar_procpaci_grupo ( nr_seq_protocolo_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
					 
/*OS 90241 em 23/05/2008 
Para o procedimento 0301080160 - ATENDIMENTO EM PSICOTERAPIA DE GRUPO  Esse código é feito de 7 em 7 pacientes para o SUS 
*/
 
 
nr_sequencia_w			bigint;
qt_registros_w			bigint	:= 0;
qt_procedimento_w		double precision	:= 0;

C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		qt_procedimento 
	from	procedimento_paciente a, 
		conta_paciente b 
	where	a.nr_interno_conta	= b.nr_interno_conta 
	and	b.nr_seq_protocolo	= nr_seq_protocolo_p 
	and	coalesce(a.cd_motivo_exc_conta::text, '') = '' 
	and	a.cd_procedimento	= 0301080160 
	and	a.ie_origem_proced	= 7 
	and	coalesce(b.ie_cancelamento::text, '') = '';
	
 

BEGIN 
 
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w, 
	qt_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	qt_registros_w	:= qt_registros_w + qt_procedimento_w;
	 
	if (mod(qt_registros_w,7)	<> 0) then 
		 
		update	procedimento_paciente 
		set	vl_procedimento		= 0, 
			vl_custo_operacional	= 0, 
			vl_medico		= 0, 
			vl_materiais		= 0 
		where	nr_sequencia		= nr_sequencia_w;
		 
		update	sus_valor_proc_paciente 
		set	vl_matmed		= 0, 
			vl_medico		= 0, 
			vl_sadt			= 0, 
			vl_ato_anestesista	= 0 
		where	nr_sequencia		= nr_sequencia_w;
	 
	end if;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE sus_ajustar_procpaci_grupo ( nr_seq_protocolo_p bigint, nm_usuario_p text) FROM PUBLIC;

