CREATE OR REPLACE PROCEDURE sus_ajusta_emissao_laudo ( nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_periodo_inicial_w		timestamp;


BEGIN 
 
begin 
select	dt_periodo_inicial 
into STRICT	dt_periodo_inicial_w 
from	conta_paciente 
where	nr_interno_conta = nr_interno_conta_p;
exception 
when others then 
	dt_periodo_inicial_w := null;
end;
 
if (dt_periodo_inicial_w IS NOT NULL AND dt_periodo_inicial_w::text <> '') then 
	begin 
	update	sus_laudo_paciente 
	set 	dt_emissao = dt_periodo_inicial_w, 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp() 
	where 	nr_interno_conta = nr_interno_conta_p 
	and	not exists (SELECT	1 
			from	sus_regra_proc x 
			where	nr_seq_regra in (7,13,28,31) 
			and	x.cd_procedimento	= cd_procedimento_solic 
			and 	x.ie_origem_proced	= ie_origem_proced);
	 
	commit;
	 
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
