CREATE OR REPLACE PROCEDURE sus_alterar_espec_cid_categ ( cd_categoria_cid_p text, cd_espec_destino_p text, nm_usuario_p text) AS $body$
DECLARE


qt_categoria_w		bigint := 0;
qt_especial_w		bigint := 0;


BEGIN

if (coalesce(cd_categoria_cid_p,'X') <> 'X') and (coalesce(cd_espec_destino_p,'X') <> 'X') then
	begin

	select	count(*)
	into STRICT	qt_categoria_w
	from	cid_categoria
	where	cd_categoria_cid = cd_categoria_cid_p;

	select	count(*)
	into STRICT	qt_especial_w
	from	cid_especialidade
	where	cd_especialidade_cid = cd_espec_destino_p;

	if (qt_categoria_w > 0) and (qt_especial_w > 0) then
		begin

		update 	cid_categoria
		set 	cd_especialidade = cd_espec_destino_p,
			dt_atualizacao	 = clock_timestamp(),
			nm_usuario	 = nm_usuario_p
		where	cd_categoria_cid = cd_categoria_cid_p;

		commit;

		end;
	end if;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
