SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_alterar_sp_proc_rim ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_partic_w			bigint;
ie_altera_sp_proc_rim_w		varchar(1) := 'S';
vl_participante_w			double precision	:= 0;	
vl_medico_w			double precision	:= 0;
vl_custo_operacional_w		procedimento_paciente.vl_custo_operacional%type;
vl_materiais_w			procedimento_paciente.vl_materiais%type;
vl_anestesista_w		procedimento_paciente.vl_anestesista%type;
vl_auxiliares_w			procedimento_paciente.vl_auxiliares%type;

c01 CURSOR FOR
	SELECT	b.nr_seq_partic
	from	procedimento_participante	b
	where	b.nr_sequencia		= nr_sequencia_p
	and	sus_obter_indicador_equipe(b.ie_funcao) in (1,2);
					

BEGIN

select	vl_medico,
	vl_custo_operacional,
	vl_materiais,
	vl_anestesista,
	vl_auxiliares
into STRICT	vl_medico_w,
	vl_custo_operacional_w,
	vl_materiais_w,
	vl_anestesista_w,
	vl_auxiliares_w
from	procedimento_paciente
where	nr_sequencia = nr_sequencia_p;

open c01;
loop
fetch c01 into
	nr_seq_partic_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	
	vl_participante_w := (vl_medico_w * 0.2308);
	
	update	procedimento_participante
	set	vl_participante	= vl_participante_w,
		pr_procedimento	= 0.2308
	where	nr_seq_partic = nr_seq_partic_w
	and	nr_sequencia  = nr_sequencia_p;

	end;
end loop;
close c01;

update	sus_valor_proc_paciente
set	vl_medico 	= trunc((vl_medico_w * 0.7692),2)
where	nr_sequencia	= nr_sequencia_p;

update	procedimento_paciente
set	vl_medico 	= trunc((vl_medico_w * 0.7692),2),
	vl_procedimento	= trunc((vl_medico_w * 0.7692),2) + coalesce(vl_participante_w,0) + coalesce(vl_custo_operacional_w, 0) + coalesce(vl_materiais_w, 0) + coalesce(vl_anestesista_w, 0) + coalesce(vl_auxiliares_w, 0)
where 	nr_sequencia  	= nr_sequencia_p;

	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
