CREATE OR REPLACE PROCEDURE sus_atualiza_aih_dados ( nr_atendimento_p bigint, cd_medico_aut_p INOUT text, cd_orgao_emissor_aih_p INOUT text) AS $body$
DECLARE

 
 
cd_estabelecimento_w		smallint;
cd_medico_autorizador_w		varchar(10);
cd_orgao_emissor_aih_w		varchar(10);
cd_pessoa_fisica_w		varchar(10);
cd_municipio_ibge_w		varchar(06);

 

BEGIN 
 
begin 
select	cd_estabelecimento, 
	cd_pessoa_fisica 
into STRICT	cd_estabelecimento_w, 
	cd_pessoa_fisica_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_p;
exception 
when others then 
	--R.aise_application_error(-20011,'Problema na leitura do atendimento, verifique se o mesmo Ã© AIH.'); 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(263435);
end;
 
begin 
select	cd_medico_autorizador 
into STRICT	cd_medico_autorizador_w 
from	sus_parametros_aih 
where	cd_estabelecimento	= cd_estabelecimento_w;
exception 
	when others then 
	--r.aise_application_error(-20011, 'Problema na leitura dos cadastros da AIH. #@#@'); 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(263436);
end;
 
if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
	begin 
	select	coalesce(max(cd_municipio_ibge),'0') 
	into STRICT	cd_municipio_ibge_w 
	from	compl_pessoa_fisica 
	where	ie_tipo_complemento	= 1 
	and	cd_pessoa_fisica	= cd_pessoa_fisica_w;
 
	if (cd_municipio_ibge_w IS NOT NULL AND cd_municipio_ibge_w::text <> '') and (cd_municipio_ibge_w <> '0') then 
		begin 
		select	cd_orgao_emissor, 
			coalesce(cd_medico_autorizador, cd_medico_autorizador_w) 
		into STRICT	cd_orgao_emissor_aih_w, 
			cd_medico_autorizador_w 
		from	sus_municipio 
		where	cd_municipio_ibge	= cd_municipio_ibge_w;
		exception 
			when others then 
			cd_medico_autorizador_w	:= cd_medico_autorizador_w;
		end;
	end if;
 
	if (coalesce(cd_orgao_emissor_aih_w::text, '') = '') then 
		select	cd_orgao_emissor_aih 
		into STRICT	cd_orgao_emissor_aih_w 
		from	sus_parametros_aih 
		where	cd_estabelecimento	= cd_estabelecimento_w;
	end if;
 
	end;
end if;
 
cd_medico_aut_p		:= cd_medico_autorizador_w;
cd_orgao_emissor_aih_p	:= cd_orgao_emissor_aih_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
