CREATE OR REPLACE PROCEDURE sus_atualiza_itens_conpaci_sh ( nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_propaci_w	bigint;
nr_seq_matpaci_w	bigint;
vl_receita_w		double precision;
qt_mat_rateio_w		bigint;


/* Busca os itens rateados da conta*/

C01 CURSOR FOR
SELECT	coalesce(nr_seq_propaci,0) nr_seq_propaci,
	coalesce(nr_seq_matpaci,0) nr_seq_matpaci,
	vl_receita
from 	w_conta_sus_sh
where	nr_interno_conta	= nr_interno_conta_p;

type 		fetch_array is table of c01%rowtype;
s_array 	fetch_array;
i		integer := 1;
type vetor is table of fetch_array index by integer;
vetor_c01_w			vetor;

BEGIN

open c01;
loop
fetch c01 bulk collect into s_array limit 100000;
	vetor_c01_w(i) := s_array;
	i := i + 1;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

for i in 1..vetor_c01_w.count loop
	begin
	s_array := vetor_c01_w(i);
	for z in 1..s_array.count loop
		begin

		nr_seq_propaci_w	:= s_array[z].nr_seq_propaci;
		nr_seq_matpaci_w	:= s_array[z].nr_seq_matpaci;
		vl_receita_w		:= s_array[z].vl_receita;

		/* Atualizar os procedimentos da conta */

		if (nr_Seq_propaci_w	> 0) then

			select	count(1)
			into STRICT	qt_mat_rateio_w
			from	w_conta_sus_sh
			where	nr_interno_conta = nr_interno_conta_p
			and	(nr_seq_matpaci IS NOT NULL AND nr_seq_matpaci::text <> '')
			and	vl_receita > 0  LIMIT 1;

			if (qt_mat_rateio_w > 0) then
				begin

				update	procedimento_paciente
				set	vl_procedimento	= vl_receita_w,
					vl_materiais	= vl_receita_w
				where	nr_sequencia	= nr_seq_propaci_w;

				end;
			end if;

		/* Atualizar os materiais da conta */

		elsif (nr_seq_matpaci_w	> 0) then
			update	material_atend_paciente
			set	vl_material	= vl_receita_w
			where	nr_sequencia	= nr_seq_matpaci_w;
		end if;

		end;
	end loop;
	end;
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
