CREATE OR REPLACE PROCEDURE sus_atualiza_reapresentacao ( nr_seq_protocolo_p bigint, ie_reapresentacao_p text, nm_usuario_p text) AS $body$
DECLARE

	 
nr_interno_conta_w	conta_paciente.nr_interno_conta%type;
	
C01 CURSOR FOR 
	SELECT	nr_interno_conta 
	from	conta_paciente 
	where	nr_seq_protocolo = nr_seq_protocolo_p 
	and	coalesce(ie_reapresentacao_sus,'N') <> coalesce(ie_reapresentacao_p,'N');					
 

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	update	conta_paciente 
	set	ie_reapresentacao_sus = coalesce(ie_reapresentacao_p,'N'), 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp() 
	where	nr_interno_conta = nr_interno_conta_w;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
