CREATE OR REPLACE PROCEDURE sus_buscar_dados_aih ( nr_atendimento_p bigint, cd_motivo_alta_p INOUT bigint, cd_cid_principal_p INOUT text, cd_cid_secundario_p INOUT text) AS $body$
DECLARE


cd_motivo_alta_w		smallint	 	:= null;
cd_cid_principal_w		varchar(10)	:= '';
cd_cid_secundario_w	varchar(10)	:= '';

c01 CURSOR FOR
	SELECT	coalesce(max(a.cd_doenca),'')
	from 	diagnostico_medico m,
		diagnostico_doenca a
	where 	a.nr_atendimento	= m.nr_atendimento
	and	a.dt_diagnostico	= m.dt_diagnostico
	and	m.nr_atendimento	= nr_atendimento_p
	and	a.ie_classificacao_doenca = 'P'
	order by a.dt_diagnostico;

c02 CURSOR FOR
	SELECT	coalesce(max(a.cd_doenca),'')
	from 	diagnostico_medico m,
		diagnostico_doenca a
	where 	a.nr_atendimento	= m.nr_atendimento
	and	a.dt_diagnostico	= m.dt_diagnostico
	and	m.nr_atendimento	= nr_atendimento_p
	and	a.ie_classificacao_doenca = 'S'
	order by a.dt_diagnostico;


BEGIN

begin
select	cd_motivo_alta
into STRICT	cd_motivo_alta_w
from	atendimento_paciente
where	nr_atendimento = nr_atendimento_p;

exception
	when others then
	cd_motivo_alta_w	:= null;
end;

open c01;
loop
fetch c01 into
	cd_cid_principal_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

open c02;
loop
fetch c02 into
	cd_cid_secundario_w;
EXIT WHEN NOT FOUND; /* apply on c02 */
end loop;
close c02;

cd_motivo_alta_p		:= cd_motivo_alta_w;
cd_cid_principal_p		:= cd_cid_principal_w;
cd_cid_secundario_p	:= cd_cid_secundario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE sus_buscar_dados_aih ( nr_atendimento_p bigint, cd_motivo_alta_p INOUT bigint, cd_cid_principal_p INOUT text, cd_cid_secundario_p INOUT text) FROM PUBLIC;

