CREATE OR REPLACE PROCEDURE sus_cancelar_laudo_aih_ws (nr_seq_laudo_pac_p bigint) AS $body$
DECLARE


nr_aih_p sus_laudo_paciente.nr_aih%type;
nr_atendimento_p sus_laudo_paciente.nr_atendimento%type;
ie_tipo_laudo_sus_p sus_laudo_paciente.ie_tipo_laudo_sus%type;


BEGIN

  begin
    select nr_aih, nr_atendimento, ie_tipo_laudo_sus
    into STRICT nr_aih_p,
    nr_atendimento_p,
    ie_tipo_laudo_sus_p
    from sus_laudo_paciente
    where nr_seq_interno = nr_seq_laudo_pac_p;
  end;
          
  if ((nr_aih_p IS NOT NULL AND nr_aih_p::text <> '') and coalesce(ie_tipo_laudo_sus_p, 00) = 1) then
  
    update sus_laudo_paciente
    set tp_situacao = 3 --CANCELADO, tabela SUS_SITUACAO_LAUDO_WS
    where nr_aih = nr_aih_p
    and nr_atendimento = nr_atendimento_p
    and ie_tipo_laudo_sus <> 2;

  else 
  
    update sus_laudo_paciente
    set tp_situacao = 3 --CANCELADO, tabela SUS_SITUACAO_LAUDO_WS
    where nr_seq_interno = nr_seq_laudo_pac_p;

  end if;

  commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
