CREATE OR REPLACE PROCEDURE sus_consiste_numero_apac ( nr_interno_conta_p bigint, nr_apac_p bigint, nm_usuario_p text, ie_tipo_consiste_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE



cd_estabelecimento_w	smallint;
ds_detalhe_w		varchar(255)	:= '';
nr_apac_digito13_w	smallint;
ds_erro_w               varchar(255)   := '';
ie_digito_apac_w	varchar(1);



BEGIN

/* Obter dados da conta e do procedimento */

select	coalesce(max(cd_estabelecimento),1)
into STRICT	cd_estabelecimento_w
from	conta_Paciente
where	nr_interno_conta	= nr_interno_conta_p;

ds_detalhe_w	:= wheb_mensagem_pck.get_texto(279593, 'DS_APAC_P=' || nr_apac_p || ';NR_CONTA_P=' || nr_interno_conta_p);

/* 20 - Número da APAC inválido */

if (Sus_Obter_Inco_Ativa(20)) and (nr_apac_p IS NOT NULL AND nr_apac_p::text <> '') and (length(nr_apac_p) <> 13) then
	if (ie_tipo_consiste_p = 1) then
        	CALL sus_gravar_inconsistencia(nr_interno_conta_p, 20, ds_detalhe_w, cd_estabelecimento_w, nm_usuario_p);
	elsif (ie_tipo_consiste_p = 2) then
        	ds_erro_w	:= ds_erro_w || '20, ';
	end if;
end if;

/* 21 - Dígito verificador da APAC inválido */

if (Sus_Obter_Inco_Ativa(21)) and (nr_apac_p IS NOT NULL AND nr_apac_p::text <> '') and (length(nr_apac_p) = 13) then
	select	consistir_digito('AIH',to_char(nr_apac_p))
	into STRICT	ie_digito_apac_w
	;
	if (ie_digito_apac_w = 'N') then
		if (ie_tipo_consiste_p = 1) then
        		CALL sus_gravar_inconsistencia(nr_interno_conta_p, 21, ds_detalhe_w, cd_estabelecimento_w, nm_usuario_p);
		elsif (ie_tipo_consiste_p = 2) then
	        	ds_erro_w	:= ds_erro_w || '21, ';
		end if;
	end if;
end if;

ds_erro_p	:= ds_erro_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
