CREATE OR REPLACE PROCEDURE sus_consiste_regra_laudo ( cd_estabelecimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, ds_mensagem_p INOUT text) AS $body$
DECLARE


nr_seq_forma_org_w		bigint := 0;
nr_seq_grupo_w			bigint := 0;
nr_seq_subgrupo_w		bigint := 0;
ie_tipo_financiamento_w		varchar(4);
ie_complexidade_w		varchar(2);
ds_mensagem_w			varchar(255);

C01 CURSOR FOR
	SELECT	ds_mensagem
	from	sus_regra_laudo a
	where	a.cd_estabelecimento	 = cd_estabelecimento_p
	and	coalesce(a.cd_procedimento,cd_procedimento_p) 		= cd_procedimento_p
	and	((coalesce(cd_procedimento::text, '') = '') or (coalesce(a.ie_origem_proced,ie_origem_proced_p)= ie_origem_proced_p))
	and	coalesce(a.nr_seq_grupo,nr_seq_grupo_w)			= nr_seq_grupo_w
	and	coalesce(a.nr_seq_subgrupo,nr_seq_subgrupo_w)		= nr_seq_subgrupo_w
	and	coalesce(a.nr_seq_forma_org,nr_seq_forma_org_w)		= nr_seq_forma_org_w
	and	coalesce(ie_complexidade,ie_complexidade_w)			= ie_complexidade_w
	and	coalesce(ie_tipo_financiamento,ie_tipo_financiamento_w)	= ie_tipo_financiamento_w
	order by 	coalesce(a.cd_procedimento,0),
			coalesce(a.nr_seq_forma_org,0),
			coalesce(a.nr_seq_subgrupo,0),
			coalesce(a.nr_seq_grupo,0),
			coalesce(ie_tipo_financiamento,'0'),
			coalesce(ie_complexidade,'0');

BEGIN

begin
select	nr_seq_forma_org,
	nr_seq_grupo,
	nr_seq_subgrupo,
	ie_complexidade,
	ie_tipo_financiamento
into STRICT	nr_seq_forma_org_w,
	nr_seq_grupo_w,
	nr_seq_subgrupo_w,
	ie_complexidade_w,
	ie_tipo_financiamento_w
from	sus_estrutura_procedimento_v a
where	a.cd_procedimento	= cd_procedimento_p
and	a.ie_origem_proced	= ie_origem_proced_p;
exception
	when others then
	null;
end;
open C01;
loop
fetch C01 into
	ds_mensagem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

ds_mensagem_p	:= ds_mensagem_w;


end;
$body$
