SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_consistir_retorno_aih ( nr_seq_protocolo_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

 
ds_erro_w		varchar(255)	:= '';
ie_status_protocolo_w	smallint;
qt_registro_w		bigint	:= 0;


BEGIN 
 
/* Protocolo deve estar fechado*/
 
select 	ie_status_protocolo 
into STRICT	ie_status_protocolo_w 
from	protocolo_convenio 
where	nr_seq_protocolo	= nr_seq_protocolo_p;
if (ie_status_protocolo_w	= 1) then 
	ds_erro_w	:= wheb_mensagem_pck.get_texto(279700);
end if;
 
/* Deve ter titulo a receber e o titulo não pode ter data de liquidação*/
 
select	count(*) 
into STRICT	qt_registro_w 
from 	titulo_receber 
where	nr_seq_protocolo	= nr_seq_protocolo_p 
and	coalesce(dt_liquidacao::text, '') = '';
if (qt_registro_w	= 0) then 
	ds_erro_w	:= ds_erro_w || wheb_mensagem_pck.get_texto(279701);
end if;
 
/* Deve ter processo de retorno para o protocolo */
 
select	count(*) 
into STRICT	qt_registro_w 
from 	sus_aih_processo 
where	nr_seq_protocolo	= nr_seq_protocolo_p;
if (qt_registro_w	= 0) then 
	ds_erro_w	:= ds_erro_w || wheb_mensagem_pck.get_texto(279702);
end if;
 
/* Todas AIH do protocolo devem estar em algum processo como pagas ou rejeitadas */
 
select	count(*) 
into STRICT	qt_registro_w 
from	conta_paciente b, 
	sus_aih c 
where	b.nr_seq_protocolo	= nr_seq_protocolo_p 
and	b.nr_interno_conta	= c.nr_interno_conta 
and	not exists ( 
	SELECT	1 from sus_aih_paga x 
	where	x.nr_aih		= c.nr_aih 
	and	x.nr_seq_aih	= c.nr_sequencia 
	and	x.nr_seq_protocolo	= nr_seq_protocolo_p) 
and	not exists ( 
	SELECT	1 from sus_aih_rejeitada y 
	where	y.nr_aih		= c.nr_aih 
	and	y.nr_seq_aih	= c.nr_sequencia 
	and	y.nr_seq_protocolo	= nr_seq_protocolo_p);
if (qt_registro_w	> 0) then 
	ds_erro_w	:= ds_erro_w || wheb_mensagem_pck.get_texto(279703);
end if;
 
if (coalesce(ds_erro_w::text, '') = '') then 
	ds_erro_w	:= wheb_mensagem_pck.get_texto(279631);
end if;
 
 
ds_erro_p	:= ds_erro_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
