SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_controla_lib_contra_ref ( nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*ie_opcao_p:
L - Liberar
D - Desfazer liberação
*/
cd_especialidade_w		integer;
cd_pessoa_fisica_w		varchar(10);
nr_seq_pes_fis_cont_ref_w	bigint;


BEGIN

if (ie_opcao_p = 'L') then
	begin

	update 	sus_contra_referencia
	set	dt_liberacao = clock_timestamp(),
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_sequencia_p;

	begin
	select	coalesce(cd_especialidade,0),
		cd_pessoa_fisica
	into STRICT	cd_especialidade_w,
		cd_pessoa_fisica_w
	from	sus_contra_referencia
	where	nr_sequencia = nr_sequencia_p;
	exception
	when others then
		cd_especialidade_w	:= 0;
		cd_pessoa_fisica_w	:= '';
	end;

	begin
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_pes_fis_cont_ref_w
	from	pessoa_fisica_contra_ref
	where	cd_pessoa_fisica = cd_pessoa_fisica_w
	and	cd_especialidade = cd_especialidade_w;
	exception
	when others then
		nr_seq_pes_fis_cont_ref_w := 0;
	end;

	update 	pessoa_fisica_contra_ref
	set 	dt_encerramento = clock_timestamp(),
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where 	nr_sequencia = nr_seq_pes_fis_cont_ref_w;

	end;
elsif (ie_opcao_p = 'D') then
	begin

	update 	sus_contra_referencia
	set	dt_liberacao  = NULL,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_sequencia_p;

	begin
	select	coalesce(cd_especialidade,0),
		cd_pessoa_fisica
	into STRICT	cd_especialidade_w,
		cd_pessoa_fisica_w
	from	sus_contra_referencia
	where	nr_sequencia = nr_sequencia_p;
	exception
	when others then
		cd_especialidade_w	:= 0;
		cd_pessoa_fisica_w	:= '';
	end;

	begin
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_pes_fis_cont_ref_w
	from	pessoa_fisica_contra_ref
	where	cd_pessoa_fisica = cd_pessoa_fisica_w
	and	cd_especialidade = cd_especialidade_w;
	exception
	when others then
		nr_seq_pes_fis_cont_ref_w := 0;
	end;

	update 	pessoa_fisica_contra_ref
	set 	dt_encerramento  = NULL,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where 	nr_sequencia = nr_seq_pes_fis_cont_ref_w;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
