SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_converte_proc_comp ( nr_interno_conta_p text, ie_opcao_p bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
cd_proc_comp_w		bigint;
ie_origem_proc_comp_w	bigint;
ie_origem_proc_conv_w	bigint;

/*
ie_opcao_p
0 - SUS
1 - AMB
2 - CBHPM
3 - PROPRIO
*/
C01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_procedimento,
		ie_origem_proced
	from	procedimento_paciente
	where	nr_interno_conta	= nr_interno_conta_p
	and	coalesce(cd_motivo_exc_conta::text, '') = '';


BEGIN
if (ie_opcao_p	= 0) then
	ie_origem_proc_conv_w	:= 7;
elsif (ie_opcao_p	= 1) then
	ie_origem_proc_conv_w	:= 1;
elsif (ie_opcao_p	= 2) then
	ie_origem_proc_conv_w	:= 5;
elsif (ie_opcao_p	= 3) then
	ie_origem_proc_conv_w	:= 4;
end if;

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	cd_procedimento_w,
	ie_origem_proced_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_origem_proc_conv_w	= 7) then
		begin
		select	coalesce(max(cd_proc_sus),0),
			coalesce(max(ie_origem_proc_sus),0)
		into STRICT	cd_proc_comp_w,
			ie_origem_proc_comp_w
		from	sus_proc_comp
		where	cd_procedimento		= cd_procedimento_w
		and	ie_origem_proced	= ie_origem_proced_w;
		end;
	else
		begin
		select	coalesce(max(cd_procedimento),0),
			coalesce(max(ie_origem_proced),0)
		into STRICT	cd_proc_comp_w,
			ie_origem_proc_comp_w
		from	sus_proc_comp
		where	cd_proc_sus		= cd_procedimento_w
		and	ie_origem_proc_sus	= ie_origem_proced_w
		and	ie_origem_proced	= ie_origem_proc_conv_w;
		end;
	end if;

	if (cd_proc_comp_w <> 0) then
		begin
		update	procedimento_paciente
		set	cd_procedimento		= cd_proc_comp_w,
			ie_origem_proced	= ie_origem_proc_comp_w
		where	nr_sequencia		= nr_sequencia_w;
		end;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
