CREATE OR REPLACE PROCEDURE sus_copiar_apac_dt_ocor (nr_apac_p bigint, nr_sequencia_p bigint, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_procedimento_w	bigint;
dt_competencia_w	timestamp;
cd_motivo_cobranca_w	smallint;
dt_ocorrencia_w		timestamp;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	sus_apac_unif
	where	nr_atendimento = nr_atendimento_p
	and	nr_apac = nr_apac_p
	and	trunc(dt_competencia,'mm') > trunc(dt_competencia_w,'mm')
	and	cd_procedimento = cd_procedimento_w;


BEGIN

select	cd_procedimento,
	dt_competencia,
	cd_motivo_cobranca,
	dt_ocorrencia
into STRICT	cd_procedimento_w,
	dt_competencia_w,
	cd_motivo_cobranca_w,
	dt_ocorrencia_w
from	sus_apac_unif
where	nr_apac = nr_apac_p
and	nr_sequencia = nr_sequencia_p;

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	sus_apac_unif
	set	cd_motivo_cobranca = cd_motivo_cobranca_w,
		dt_ocorrencia = dt_ocorrencia_w
	where	nr_sequencia = nr_sequencia_w
	and	nr_apac = nr_apac_p;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
