CREATE OR REPLACE PROCEDURE sus_define_periodo_aih ( nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_periodo_inicial_w	timestamp;
dt_periodo_final_w	timestamp;
nr_aih_w		bigint;
nr_sequencia_w		bigint;


BEGIN 
 
begin 
select	dt_periodo_inicial, 
	dt_periodo_final 
into STRICT	dt_periodo_inicial_w, 
	dt_periodo_final_w 
from	conta_paciente 
where	nr_interno_conta = nr_interno_conta_p;
exception 
	when others then 
	--R.aise_application_error(-20011,'Problemas ao obter as datas do per√≠odo da conta!'); 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(263439);
	end;
 
begin	 
select	nr_aih, 
	nr_sequencia 
into STRICT	nr_aih_w, 
	nr_sequencia_w 
from	sus_aih_unif 
where	nr_interno_conta = nr_interno_conta_p 
and	ie_identificacao_aih <> 5;
exception 
	when others then 
	nr_aih_w	:= null;
	nr_sequencia_w	:= null;
	end;
	 
if (nr_aih_w IS NOT NULL AND nr_aih_w::text <> '') and (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then 
	begin 
	update	sus_aih_unif 
	set	dt_inicial	= dt_periodo_inicial_w, 
		dt_final	= dt_periodo_final_w 
	where	nr_aih		= nr_aih_w 
	and	nr_sequencia	= nr_sequencia_w;
	end;
end if;	
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
