SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_definir_aih ( nr_aih_p bigint, nr_seq_aih_p bigint, nr_seq_registro_p bigint, ie_registro_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
/* IE_REGISTRO_P 
	3 - Conta paciente 
	13 - Registro civil 
	25 - Laudo paciente 
*/
 
 
nr_interno_conta_w	bigint;
ie_status_acerto_w	smallint;

 

BEGIN 
 
if (ie_registro_p	= 3) then 
	begin 
	select	nr_interno_conta 
	into STRICT	nr_interno_conta_w 
	from	sus_aih_unif 
	where	nr_sequencia	= nr_seq_aih_p 
	and	nr_aih		= nr_aih_p;
	exception 
		when others then 
		nr_interno_conta_w	:= null;
	end;
	 
	if (nr_interno_conta_w IS NOT NULL AND nr_interno_conta_w::text <> '') then 
		select	ie_status_acerto	 
		into STRICT	ie_status_acerto_w 
		from	conta_paciente 
		where	nr_interno_conta	= nr_interno_conta_w;
	end if;	
 
	if (ie_status_acerto_w = 1) or (coalesce(nr_interno_conta_w::text, '') = '') then	 
		update	sus_aih_unif 
		set	nr_interno_conta	 = NULL, 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao		= clock_timestamp() 
		where	nr_interno_conta	= nr_seq_registro_p;
	 
		update	sus_aih_unif 
		set	nr_interno_conta	= nr_seq_registro_p, 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao		= clock_timestamp() 
		where	nr_aih			= nr_aih_p 
		and	nr_sequencia		= nr_seq_aih_p;	
	end if;
elsif (ie_registro_p	= 13) then 
	update	sus_registro_civil 
	set	nr_aih     	= nr_aih_p, 
		nr_seq_aih   	= nr_seq_aih_p 
	where	nr_sequencia	= nr_seq_registro_p;
elsif (ie_registro_p	= 25) then 
	update	sus_laudo_paciente 
	set	nr_aih     	= nr_aih_p, 
		nr_seq_aih   	= nr_seq_aih_p 
	where	nr_seq_interno	= nr_seq_registro_p;
end if;
 
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE sus_definir_aih ( nr_aih_p bigint, nr_seq_aih_p bigint, nr_seq_registro_p bigint, ie_registro_p bigint, nm_usuario_p text) FROM PUBLIC;

