SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_definir_apac ( nr_seq_apac_p bigint, nr_seq_registro_p bigint, ie_registro_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
/* IE_REGISTRO_P 
	3 - Conta paciente	 
*/
 
nr_interno_conta_w	bigint;
ie_status_acerto_w	smallint;
ds_erro_w		varchar(255);


BEGIN 
 
if (ie_registro_p	= 3) then 
	begin 
	select	nr_interno_conta 
	into STRICT	nr_interno_conta_w 
	from	sus_apac_unif 
	where	nr_sequencia	= nr_seq_apac_p;
	exception 
		when others then 
		nr_interno_conta_w	:= null;
	end;
	 
	if (nr_interno_conta_w IS NOT NULL AND nr_interno_conta_w::text <> '') then 
		select	ie_status_acerto	 
		into STRICT	ie_status_acerto_w 
		from	conta_paciente 
		where	nr_interno_conta	= nr_interno_conta_w;
	end if;	
	 
	if (ie_status_acerto_w = 1) or (coalesce(nr_interno_conta_w::text, '') = '') then 
		begin 
		update	sus_apac_unif 
		set	nr_interno_conta	 = NULL, 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao		= clock_timestamp() 
		where	nr_interno_conta	= nr_seq_registro_p;
 
		update	sus_apac_unif 
		set	nr_interno_conta	= nr_seq_registro_p, 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao		= clock_timestamp() 
		where	nr_sequencia		= nr_seq_apac_p;	
		 
		ds_erro_w := Sus_Atualiza_Proced_Apac(nr_seq_apac_p, nm_usuario_p, ds_erro_w);
		end;
	end if;
end if;
 
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
