SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_desfazer_retorno_lote ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
BEGIN
 
begin 
update 	sus_lote_autor 
set	dt_retorno		 = NULL, 
	nm_usuario_retorno	 = NULL, 
	nm_usuario		= nm_usuario_p, 
	dt_atualizacao		= clock_timestamp() 
where	nr_sequencia		= nr_seq_lote_p;
exception 
when others then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(200539,'NR_SEQ_LOTE='||nr_seq_lote_p);
end;
 
begin 
update	sus_laudo_paciente 
set	dt_retorno_secr		 = NULL, 
	nr_aih			 = NULL, 
	dt_atualizacao		=	clock_timestamp(), 
	nm_usuario		=	nm_usuario_p 
where	nr_seq_lote		=	nr_seq_lote_p 
and	not exists (	SELECT 	1 
			from	w_sus_importacao_ach x 
			where	x.cdideaih = nr_aih);
exception 
when others then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(200540,'NR_SEQ_LOTE='||nr_seq_lote_p);
end;
 
begin 
delete from sus_aih_unif 
where	nr_interno_conta in (	SELECT 	p.nr_interno_conta 
				from	sus_laudo_paciente p, 
					sus_lote_autor l 
				where	p.nr_seq_lote = l.nr_sequencia 
				and	l.nr_sequencia = nr_seq_lote_p) 
and	not exists (	select 	1 
			from	w_sus_importacao_ach x 
			where	x.cdideaih = nr_aih);
exception 
when others then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(200541,'NR_SEQ_LOTE='||nr_seq_lote_p);
end;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
