CREATE OR REPLACE PROCEDURE sus_desvincular_aih ( nr_seq_registro_p bigint, ie_registro_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
/* IE_REGISTRO_P 
	3 - Conta paciente 
	13 - Registro civil 
	25 - Laudo 
*/
 
 

BEGIN 
 
if (ie_registro_p	= 3) then 
	update	sus_aih_unif 
	set	nr_interno_conta	 = NULL, 
		nm_usuario		= nm_usuario_p, 
		dt_atualizacao		= clock_timestamp() 
	where	nr_interno_conta	= nr_seq_registro_p;
elsif (ie_registro_p	= 13) then 
	update	sus_registro_civil 
	set	nr_aih     	 = NULL, 
		nr_seq_aih   	 = NULL 
	where	nr_sequencia	= nr_seq_registro_p;
elsif (ie_registro_p	= 25) then 
	update	sus_laudo_paciente 
	set	nr_aih     	 = NULL, 
		nr_seq_aih   	 = NULL 
	where	nr_seq_interno	= nr_seq_registro_p;
end if;
 
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
