CREATE OR REPLACE PROCEDURE sus_duplicar_apac_protocolo ( Nr_seq_protocolo_p bigint, Nm_usuario_p text) AS $body$
DECLARE

 
nr_atendimento_w	bigint;
nr_sequencia_w		bigint;
nr_apac_w		bigint;
qt_apac_w		integer;

C01 CURSOR FOR 
	SELECT	a.nr_atendimento, 
		b.nr_sequencia, 
		b.nr_apac 
	from	sus_apac_movto	b, 
		conta_paciente	a 
	where	a.nr_interno_conta	= b.nr_interno_conta 
	and	a.nr_seq_protocolo	= nr_seq_protocolo_p 
	and	b.ie_tipo_apac		not in (3,4);


BEGIN 
OPEN C01;
	LOOP 
	FETCH C01 into 
		nr_atendimento_w, 
		nr_sequencia_w, 
		nr_apac_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		select	count(*) 
		into STRICT	qt_apac_w 
		from	sus_apac_movto 
		where	nr_apac	= nr_apac_w;
 
		if (qt_apac_w < 3) then 
			CALL duplicar_apac(nr_atendimento_w, nr_sequencia_w, nm_usuario_p);
		end if;
	END LOOP;
CLOSE C01;
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
