SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sus_duplicar_bpa_unif ( nr_sequencia_p sus_bpa_unif.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE


qt_bpa_atend_w		bigint := 0;
nr_atendimento_w	sus_bpa_unif.nr_atendimento%type;
ds_erro_w		varchar(4000);


BEGIN

select	coalesce(max(nr_atendimento),0)
into STRICT	nr_atendimento_w
from	sus_bpa_unif
where	nr_sequencia = nr_sequencia_p;

select	count(1)
into STRICT	qt_bpa_atend_w
from	sus_bpa_unif
where	nr_atendimento = nr_atendimento_w
and	(nr_bpa IS NOT NULL AND nr_bpa::text <> '')
and	coalesce(nr_interno_conta::text, '') = ''  LIMIT 1;

if (qt_bpa_atend_w = 0) then
	begin

	begin
	insert into sus_bpa_unif(
		cd_carater_atendimento,
		cd_estabelecimento,
		dt_atualizacao,
		dt_atualizacao_nrec,
		dt_emissao,
		nm_usuario,
		nm_usuario_nrec,
		nr_atendimento,
		nr_bpa,
		nr_interno_conta,
		nr_sequencia)
	SELECT	cd_carater_atendimento,
		cd_estabelecimento,
		clock_timestamp(),
		clock_timestamp(),
		dt_emissao,
		nm_usuario_p,
		nm_usuario_p,
		nr_atendimento,
		nr_bpa,
		null,
		nextval('sus_bpa_unif_seq')
	from	sus_bpa_unif
	where	nr_sequencia = nr_sequencia_p;
	exception
	when others then
		ds_erro_w	:= substr(sqlerrm(SQLSTATE),1,4000);
		CALL wheb_mensagem_pck.exibir_mensagem_abort(275051,'ERRO_P='||ds_erro_w);
	end;

	commit;

	end;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(275059);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
